mapper.update:
update customer
set username = #{username,jdbcType=VARCHAR},
  `password` = #{password,jdbcType=VARCHAR},
  balance = #{balance,jdbcType=REAL}
where cid = #{cid,jdbcType=VARCHAR}

mapper.select:
select cid, username, `password`, balance
from customer
where cid = #{cid,jdbcType=VARCHAR}
