mapper.delete:
delete from t_warehouse_sku
where warehouse_id = #{warehouse_id,jdbcType=INTEGER}
  and sku_id = #{sku_id,jdbcType=INTEGER}

mapper.update:
update t_warehouse_sku
set num = #{num,jdbcType=INTEGER},
  unit = #{unit,jdbcType=VARCHAR}
where warehouse_id = #{warehouse_id,jdbcType=INTEGER}
  and sku_id = #{sku_id,jdbcType=INTEGER}

mapper.select:
select warehouse_id, sku_id, num, unit
from t_warehouse_sku
where warehouse_id = #{warehouse_id,jdbcType=INTEGER}
  and sku_id = #{sku_id,jdbcType=INTEGER}
